import React from 'react';
import { useLocation } from 'react-router-dom';
import Navbar from './SideNavBar/SideNavBar';

const Layout = ({ children }) => {
  const location = useLocation();
  const isLoginPage = location.pathname === "/login" || location.pathname === "/";

  // Si es la p치gina de login, mostrar solo el contenido sin layout
  if (isLoginPage) {
    return <>{children}</>;
  }

  // Para otras p치ginas, mostrar el layout completo con navbar
  return (
    <div className="flex h-screen">
      <aside className="w-64 min-h-screen bg-white shadow-lg">
        <Navbar />
      </aside>
      
      <main className="flex-1 bg-gray-50 overflow-auto">
        <div className="p-6">
          {children}
        </div>
      </main>
    </div>
  );
};

export default Layout;
