import React, { useState, useMemo } from 'react';
import { Pencil, Trash2, UserCircle2, ChevronLeft, ChevronRight, Search } from 'lucide-react';
import Swal from 'sweetalert2';
import UserModal from './AddUserModal';

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    // ... your existing users data
  ]);

  // Filter users based on search term
  const filteredUsers = useMemo(() => {
    return users.filter(user => 
      user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.profile.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [users, searchTerm]);

  // Calculate pagination
  const totalPages = Math.ceil(filteredUsers.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentUsers = filteredUsers.slice(startIndex, endIndex);

  // Your existing handlers
  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (user) => {
    // ... your existing delete logic
  };

  const toggleStatus = (userId) => {
    // ... your existing status toggle logic
  };

  const handleSave = (userData) => {
    // ... your existing save logic
  };

  return (
    <div className="w-full">
      {/* Header Section */}
      <div className="flex justify-between items-center p-4 bg-gray-50">
        <div className="flex items-center space-x-4">
          <span className="text-gray-600">Mostrar</span>
          <select 
            className="border rounded px-2 py-1 bg-white"
            value={itemsPerPage}
            onChange={(e) => setItemsPerPage(Number(e.target.value))}
          >
            <option value={10}>10</option>
            <option value={25}>25</option>
            <option value={50}>50</option>
          </select>
          <span className="text-gray-600">registros</span>
        </div>
        
        <div className="flex items-center">
          <span className="text-gray-600 mr-2">Buscar:</span>
          <div className="relative">
            <input
              type="text"
              className="border rounded px-3 py-1 pl-8 w-64"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Buscar..."
            />
            <Search className="absolute left-2 top-2 h-4 w-4 text-gray-400" />
          </div>
        </div>
      </div>

      {/* Table Section */}
      <div className="overflow-x-auto">
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-gray-100">
              <th className="border px-4 py-2 text-left">#</th>
              <th className="border px-4 py-2 text-left">Nombre</th>
              <th className="border px-4 py-2 text-left">Usuario</th>
              <th className="border px-4 py-2 text-left">Foto</th>
              <th className="border px-4 py-2 text-left">Perfil</th>
              <th className="border px-4 py-2 text-left">Estado</th>
              <th className="border px-4 py-2 text-left">Último login</th>
              <th className="border px-4 py-2 text-left">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {currentUsers.length > 0 ? (
              currentUsers.map((user) => (
                <tr key={user.id} className="hover:bg-gray-50">
                  <td className="border px-4 py-2">{user.id}</td>
                  <td className="border px-4 py-2">{user.name}</td>
                  <td className="border px-4 py-2">{user.username}</td>
                  <td className="border px-4 py-2">
                    <UserCircle2 className="h-8 w-8 text-gray-400" />
                  </td>
                  <td className="border px-4 py-2">{user.profile}</td>
                  <td className="border px-4 py-2">
                    <span 
                      className={`px-2 py-1 rounded-full text-sm ${
                        user.status === 'Activado' 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-red-100 text-red-800'
                      }`}
                      onClick={() => toggleStatus(user.id)}
                      style={{ cursor: 'pointer' }}
                    >
                      {user.status}
                    </span>
                  </td>
                  <td className="border px-4 py-2">{user.lastLogin}</td>
                  <td className="border px-4 py-2">
                    <div className="flex space-x-2">
                      <button 
                        onClick={() => handleEdit(user)}
                        className="p-1 hover:bg-gray-100 rounded"
                      >
                        <Pencil className="h-4 w-4 text-blue-600" />
                      </button>
                      <button 
                        onClick={() => handleDelete(user)}
                        className="p-1 hover:bg-gray-100 rounded"
                      >
                        <Trash2 className="h-4 w-4 text-red-600" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="8" className="border px-4 py-8 text-center text-gray-500">
                  Ningún dato disponible en esta tabla
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination Section */}
      <div className="flex justify-between items-center p-4 bg-gray-50">
        <div className="text-sm text-gray-600">
          Mostrando registros del {startIndex + 1} al {Math.min(endIndex, filteredUsers.length)} de un total de {filteredUsers.length}
        </div>
        <div className="flex space-x-2">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className="px-3 py-1 border rounded hover:bg-gray-100 disabled:opacity-50"
          >
            Anterior
          </button>
          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className="px-3 py-1 border rounded hover:bg-gray-100 disabled:opacity-50"
          >
            Siguiente
          </button>
        </div>
      </div>

      <UserModal 
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedUser(null);
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  );
};

export default UserManagement;
