import React, { useState } from "react";
import './LoginPage.css';
import backgroundImage from '../assets/img/13.jpg';
import logoImage from '../assets/img/12.png';

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:3001/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Login exitoso
        console.log("Login exitoso:", data);
        // Aquí puedes redirigir al usuario o actualizar el estado de la aplicación
        // Por ejemplo: history.push('/dashboard');
      } else {
        // Login fallido
        setError(data.message || "Error al iniciar sesión. Por favor, intenta de nuevo.");
      }
    } catch (error) {
      setError("Error de conexión. Por favor, verifica tu conexión a internet e intenta de nuevo.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-background" style={{backgroundImage: `url(${backgroundImage})`}}></div>
      <div className="login-card">
        {/* Logo */}
        <div className="text-center mb-4">
          <img src={logoImage || "/placeholder.svg"} alt="Logo" className="login-logo" />
        </div>

        {/* Formulario de login */}
        <form onSubmit={handleLogin}>
          <div className="form-group mb-4">
            <input
              type="text"
              className="form-control custom-input"
              id="username"
              placeholder="Usuario"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <label htmlFor="username" className="custom-label">Usuario</label>
          </div>
          <div className="form-group mb-4">
            <input
              type="password"
              className="form-control custom-input"
              id="password"
              placeholder="Contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <label htmlFor="password" className="custom-label">Contraseña</label>
          </div>
          {error && <div className="alert alert-danger">{error}</div>}
          <button
            type="submit"
            className="btn btn-primary login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
