"use client"

import { useState, useEffect } from "react"
import Swal from "sweetalert2"
import $ from "jquery"
import "datatables.net-bs5"
import "datatables.net-bs5/css/dataTables.bootstrap5.min.css"
import "bootstrap/dist/css/bootstrap.min.css"
import UserModal from "./AddUserModal"

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [selectedUser, setSelectedUser] = useState(null)
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32",
    },
    {
      id: 2,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32",
    },
    {
      id: 3,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Desactivado",
      lastLogin: "2017-12-11 12:05:32",
    },
  ])

  useEffect(() => {
    const table = $("#usersTable").DataTable({
      language: {
        url: "//cdn.datatables.net/plug-ins/1.13.7/i18n/es-ES.json",
      },
      columns: [
        { data: "id" },
        { data: "name" },
        { data: "username" },
        {
          data: null,
          render: (data, type, row) =>
            '<span class="user-icon"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-user-circle-2"><path d="M18 20a6 6 0 0 0-12 0"/><circle cx="12" cy="10" r="4"/><circle cx="12" cy="12" r="10"/></svg></span>',
        },
        { data: "profile" },
        {
          data: "status",
          render: (data, type, row) =>
            `<span class="badge ${data === "Activado" ? "bg-success" : "bg-danger"}">${data}</span>`,
        },
        { data: "lastLogin" },
        {
          data: null,
          render: (data, type, row) => `
              <button class="btn btn-sm btn-primary me-1 btn-edit" data-id="${row.id}"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-pencil"><path d="M17 3a2.85 2.83 0 1 1 4 4L7.5 20.5 2 22l1.5-5.5Z"/><path d="m15 5 4 4"/></svg></button>
              <button class="btn btn-sm btn-danger btn-delete" data-id="${row.id}"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-trash-2"><path d="M3 6h18"/><path d="M19 6v14c0 1-1 2-2 2H7c-1 0-2-1-2-2V6"/><path d="M8 6V4c0-1 1-2 2-2h4c1 0 2 1 2 2v2"/><line x1="10" x2="10" y1="11" y2="17"/><line x1="14" x2="14" y1="11" y2="17"/></svg></button>
            `,
        },
      ],
    })

    $("#usersTable").on("click", ".btn-edit", function () {
      const userId = $(this).data("id")
      const user = users.find((u) => u.id === userId)
      handleEdit(user)
    })

    $("#usersTable").on("click", ".btn-delete", function () {
      const userId = $(this).data("id")
      const user = users.find((u) => u.id === userId)
      handleDelete(user)
    })

    return () => {
      table.destroy()
    }
  }, [users])

  const handleEdit = (user) => {
    setSelectedUser(user)
    setIsModalOpen(true)
  }

  const handleDelete = (user) => {
    Swal.fire({
      title: "¿Estás seguro?",
      text: `¿Deseas eliminar al usuario ${user.name}?`,
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#c59951",
      cancelButtonColor: "#d33",
      confirmButtonText: "Sí, eliminar",
      cancelButtonText: "Cancelar",
    }).then((result) => {
      if (result.isConfirmed) {
        setUsers(users.filter((u) => u.id !== user.id))
        Swal.fire("Eliminado!", "El usuario ha sido eliminado.", "success")
      }
    })
  }

  const toggleStatus = (userId) => {
    setUsers(
      users.map((user) => {
        if (user.id === userId) {
          const newStatus = user.status === "Activado" ? "Desactivado" : "Activado"
          return { ...user, status: newStatus }
        }
        return user
      }),
    )
  }

  const handleSave = (userData) => {
    if (userData.id) {
      setUsers(users.map((user) => (user.id === userData.id ? { ...user, ...userData } : user)))
    } else {
      setUsers([...users, { ...userData, id: users.length + 1 }])
    }
    setIsModalOpen(false)
    setSelectedUser(null)
  }

  return (
    <div className="container-fluid mt-4">
      <div className="row mb-3">
        <div className="col">
          <h2>Administrar usuarios</h2>
        </div>
        <div className="col text-end">
          <button
            className="btn btn-primary"
            onClick={() => {
              setSelectedUser(null)
              setIsModalOpen(true)
            }}
          >
            Agregar usuario
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-body">
          <table id="usersTable" className="table table-striped" style={{ width: "100%" }}>
            <thead>
              <tr>
                <th>#</th>
                <th>Nombre</th>
                <th>Usuario</th>
                <th>Foto</th>
                <th>Perfil</th>
                <th>Estado</th>
                <th>Último login</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.username}</td>
                  <td></td>
                  <td>{user.profile}</td>
                  <td>{user.status}</td>
                  <td>{user.lastLogin}</td>
                  <td></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <UserModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false)
          setSelectedUser(null)
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  )
}

export default UserManagement
