import React, { useState } from "react"

function LoginPage() {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")

  const handleLogin = (e) => {
    e.preventDefault()
    // Aquí iría la lógica de autenticación
    console.log("Iniciar sesión con:", username, password)
  }

  return (
    <div className="bg-red-500 p-4 text-white">
      {/* Imagen de fondo */}
      <div className="absolute inset-0 z-0">
        <img src="/placeholder.svg?height=1080&width=1920" alt="Fondo" className="w-full h-full object-cover" />
      </div>

      {/* Cuadro semitransparente */}
      <div className="z-10 p-8 bg-white bg-opacity-80 rounded-lg shadow-xl w-full max-w-md">
        {/* Logo */}
        <div className="flex justify-center mb-8">
          <img src="/placeholder.svg?height=100&width=200" alt="Logo" className="w-48 h-auto" />
        </div>

        {/* Formulario de login */}
        <form onSubmit={handleLogin} className="space-y-6">
          <div>
            <input
              type="text"
              placeholder="Usuario"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div>
            <input
              type="password"
              placeholder="Contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button
            type="submit"
            className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-75"
          >
            Iniciar Sesión
          </button>
        </form>
      </div>
    </div>
  )
}

export default LoginPage
