import { React, useState, useMemo } from 'react';
import { Pencil, Trash2, UserCircle2, Search, ChevronUp, ChevronDown } from 'lucide-react';
import Swal from 'sweetalert2';
import styles from './usuarios.module.css';
import UserModal from './AddUserModal';

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [itemsPerPage, setItemsPerPage] = useState(5); // Estado para la cantidad de registros por página

  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 2,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 3,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Desactivado",
      lastLogin: "2017-12-11 12:05:32"
    }
  ]);

  // Sorting function
  const requestSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  // Filtered and sorted users
  const filteredAndSortedUsers = useMemo(() => {
    let filtered = [...users];
    
    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(user =>
        Object.values(user).some(value =>
          value.toString().toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    }

    // Apply sorting
    if (sortConfig.key) {
      filtered.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
      });
    }

    return filtered;
  }, [users, searchTerm, sortConfig]);

  // Pagination
  const totalPages = Math.ceil(filteredAndSortedUsers.length / itemsPerPage);
  const paginatedUsers = filteredAndSortedUsers.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (user) => {
    Swal.fire({
      title: '¿Estás seguro?',
      text: `¿Deseas eliminar al usuario ${user.name}?`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#c59951',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sí, eliminar',
      cancelButtonText: 'Cancelar'
    }).then((result) => {
      if (result.isConfirmed) {
        setUsers(users.filter(u => u.id !== user.id));
        Swal.fire(
          'Eliminado!',
          'El usuario ha sido eliminado.',
          'success'
        );
      }
    });
  };

  const toggleStatus = (userId) => {
    setUsers(users.map(user => {
      if (user.id === userId) {
        const newStatus = user.status === "Activado" ? "Desactivado" : "Activado";
        return { ...user, status: newStatus };
      }
      return user;
    }));
  };

  const handleSave = (userData) => {
    if (userData.id) {
      setUsers(users.map(user => 
        user.id === userData.id ? { ...user, ...userData } : user
      ));
    } else {
      setUsers([...users, { ...userData, id: users.length + 1 }]);
    }
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  // Render sort indicator
  const SortIndicator = ({ columnKey }) => {
    if (sortConfig.key !== columnKey) {
      return <ChevronUp className="opacity-20 w-4 h-4 inline-block ml-1" />;
    }
    return sortConfig.direction === 'asc' ? 
      <ChevronUp className="w-4 h-4 inline-block ml-1" /> : 
      <ChevronDown className="w-4 h-4 inline-block ml-1" />;
  };

  return (
    <div className={styles.containerFluid1}>
      <div className={styles.headerSection1}>
        <h2 className={styles.title1}>Administrar usuarios</h2>
      </div>

      <div className="flex justify-between items-center mb-4">
        <button 
          className={styles.btnAdd1} 
          onClick={() => {
            setSelectedUser(null);
            setIsModalOpen(true);
          }}
        >
          Agregar usuario
        </button>

        <div className="flex items-center gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Buscar usuarios..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                setCurrentPage(1);
              }}
              className="pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div className="flex items-center gap-2">
            <label htmlFor="itemsPerPage" className="text-sm text-gray-500">Mostrar:</label>
            <select
              id="itemsPerPage"
              value={itemsPerPage}
              onChange={(e) => {
                setItemsPerPage(Number(e.target.value));
                setCurrentPage(1); // Resetear a la primera página al cambiar la cantidad de registros por página
              }}
              className="pl-2 pr-8 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={20}>20</option>
              <option value={50}>50</option>
            </select>
          </div>
        </div>
      </div>

      <div className={styles.tableWrapper1}>
        <div className={styles.tableContainer1}>
          <table className={styles.usersTable1}>
            <thead>
              <tr>
                <th onClick={() => requestSort('id')} className="cursor-pointer">
                  # <SortIndicator columnKey="id" />
                </th>
                <th onClick={() => requestSort('name')} className="cursor-pointer">
                  Nombre <SortIndicator columnKey="name" />
                </th>
                <th onClick={() => requestSort('username')} className="cursor-pointer">
                  Usuario <SortIndicator columnKey="username" />
                </th>
                <th>Foto</th>
                <th onClick={() => requestSort('profile')} className="cursor-pointer">
                  Perfil <SortIndicator columnKey="profile" />
                </th>
                <th onClick={() => requestSort('status')} className="cursor-pointer">
                  Estado <SortIndicator columnKey="status" />
                </th>
                <th onClick={() => requestSort('lastLogin')} className="cursor-pointer">
                  Último login <SortIndicator columnKey="lastLogin" />
                </th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {paginatedUsers.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.username}</td>
                  <td>
                    <UserCircle2 size={32} className={styles.userIcon1} />
                  </td>
                  <td>{user.profile}</td>
                  <td>
                    <span 
                      className={`${styles.statusBadge1} ${user.status === 'Activado' ? styles.active1 : styles.inactive1}`}
                      onClick={() => toggleStatus(user.id)}
                      style={{ cursor: 'pointer' }}
                    >
                      {user.status}
                    </span>
                  </td>
                  <td>{user.lastLogin}</td>
                  <td>
                    <div className={styles.actionButtons1}>
                      <button className={styles.btnEdit1} onClick={() => handleEdit(user)}>
                        <Pencil size={16} />
                      </button>
                      <button className={styles.btnDelete1} onClick={() => handleDelete(user)}>
                        <Trash2 size={16} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Pagination controls */}
      <div className="flex justify-between items-center mt-4 px-4">
        <div className="text-sm text-gray-500">
          Mostrando {Math.min(itemsPerPage, filteredAndSortedUsers.length)} de {filteredAndSortedUsers.length} usuarios
        </div>
        <div className="flex gap-2">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className="px-3 py-1 border rounded-md disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100"
          >
            Anterior
          </button>
          <div className="flex items-center gap-1">
            {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
              <button
                key={page}
                onClick={() => setCurrentPage(page)}
                className={`px-3 py-1 border rounded-md ${
                  currentPage === page
                    ? 'bg-blue-500 text-white'
                    : 'hover:bg-gray-100'
                }`}
              >
                {page}
              </button>
            ))}
          </div>
          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className="px-3 py-1 border rounded-md disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100"
          >
            Siguiente
          </button>
        </div>
      </div>

      <UserModal 
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedUser(null);
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  );
};

export default UserManagement;
