import React, { useState, useEffect } from 'react';
import { Pencil, Trash2, UserCircle2 } from 'lucide-react';
import Swal from 'sweetalert2';
import $ from 'jquery';
import 'datatables.net-bs5';
import 'datatables.net-bs5/css/dataTables.bootstrap5.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import UserModal from './AddUserModal';

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 2,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 3,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Desactivado",
      lastLogin: "2017-12-11 12:05:32"
    }
  ]);

  useEffect(() => {
    const table = $('#usersTable').DataTable({
      language: {
        url: '//cdn.datatables.net/plug-ins/1.13.7/i18n/es-ES.json',
      },
      columns: [
        { data: 'id' },
        { data: 'name' },
        { data: 'username' },
        { 
          data: null,
          render: function(data, type, row) {
            return '<span class="user-icon"><UserCircle2 size={24} /></span>';
          }
        },
        { data: 'profile' },
        { 
          data: 'status',
          render: function(data, type, row) {
            return `<span class="badge ${data === 'Activado' ? 'bg-success' : 'bg-danger'}">${data}</span>`;
          }
        },
        { data: 'lastLogin' },
        {
          data: null,
          render: function(data, type, row) {
            return `
              <button class="btn btn-sm btn-primary me-1 btn-edit" data-id="${row.id}"><Pencil size={16} /></button>
              <button class="btn btn-sm btn-danger btn-delete" data-id="${row.id}"><Trash2 size={16} /></button>
            `;
          }
        }
      ]
    });

    $('#usersTable').on('click', '.btn-edit', function() {
      const userId = $(this).data('id');
      const user = users.find(u => u.id === userId);
      handleEdit(user);
    });

    $('#usersTable').on('click', '.btn-delete', function() {
      const userId = $(this).data('id');
      const user = users.find(u => u.id === userId);
      handleDelete(user);
    });

    return () => {
      table.destroy();
    };
  }, [users]);

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (user) => {
    Swal.fire({
      title: '¿Estás seguro?',
      text: `¿Deseas eliminar al usuario ${user.name}?`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#c59951',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sí, eliminar',
      cancelButtonText: 'Cancelar'
    }).then((result) => {
      if (result.isConfirmed) {
        setUsers(users.filter(u => u.id !== user.id));
        Swal.fire(
          'Eliminado!',
          'El usuario ha sido eliminado.',
          'success'
        );
      }
    });
  };

  const toggleStatus = (userId) => {
    setUsers(users.map(user => {
      if (user.id === userId) {
        const newStatus = user.status === "Activado" ? "Desactivado" : "Activado";
        return { ...user, status: newStatus };
      }
      return user;
    }));
  };

  const handleSave = (userData) => {
    if (userData.id) {
      setUsers(users.map(user => 
        user.id === userData.id ? { ...user, ...userData } : user
      ));
    } else {
      setUsers([...users, { ...userData, id: users.length + 1 }]);
    }
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  return (
    <div className="container-fluid mt-4">
      <div className="row mb-3">
        <div className="col">
          <h2>Administrar usuarios</h2>
        </div>
        <div className="col text-end">
          <button className="btn btn-primary" onClick={() => {
            setSelectedUser(null);
            setIsModalOpen(true);
          }}>
            Agregar usuario
          </button>
        </div>
      </div>

      <div className="card">
        <div className="card-body">
          <table id="usersTable" className="table table-striped" style={{width: '100%'}}>
            <thead>
              <tr>
                <th>#</th>
                <th>Nombre</th>
                <th>Usuario</th>
                <th>Foto</th>
                <th>Perfil</th>
                <th>Estado</th>
                <th>Último login</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.username}</td>
                  <td><UserCircle2 size={24} /></td>
                  <td>{user.profile}</td>
                  <td>
                    <span 
                      className={`badge ${user.status === 'Activado' ? 'bg-success' : 'bg-danger'}`}
                      onClick={() => toggleStatus(user.id)}
                      style={{ cursor: 'pointer' }}
                    >
                      {user.status}
                    </span>
                  </td>
                  <td>{user.lastLogin}</td>
                  <td>
                    <button className="btn btn-sm btn-primary me-1" onClick={() => handleEdit(user)}>
                      <Pencil size={16} />
                    </button>
                    <button className="btn btn-sm btn-danger" onClick={() => handleDelete(user)}>
                      <Trash2 size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <UserModal 
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedUser(null);
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  );
};

export default UserManagement;
