"use client";
import React, { useState } from "react";
import DataTable from "react-data-table-component";
import { Pencil, Trash2, UserCircle2 } from "lucide-react";
import Swal from "sweetalert2";
import styles from "./usuarios.module.css";
import UserModal from "./AddUserModal";

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32",
    },
    {
      id: 2,
      name: "Usuario Secundario",
      username: "user2",
      profile: "Usuario",
      status: "Activado",
      lastLogin: "2023-05-10 15:30:20",
    },
    {
      id: 3,
      name: "Usuario Inactivo",
      username: "user3",
      profile: "Usuario",
      status: "Desactivado",
      lastLogin: "2022-08-01 10:20:15",
    },
  ]);

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (user) => {
    Swal.fire({
      title: "¿Estás seguro?",
      text: `¿Deseas eliminar al usuario ${user.name}?`,
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#c59951",
      cancelButtonColor: "#d33",
      confirmButtonText: "Sí, eliminar",
      cancelButtonText: "Cancelar",
    }).then((result) => {
      if (result.isConfirmed) {
        setUsers(users.filter((u) => u.id !== user.id));
        Swal.fire("Eliminado!", "El usuario ha sido eliminado.", "success");
      }
    });
  };

  const toggleStatus = (userId) => {
    setUsers(
      users.map((user) => {
        if (user.id === userId) {
          const newStatus = user.status === "Activado" ? "Desactivado" : "Activado";
          return { ...user, status: newStatus };
        }
        return user;
      })
    );
  };

  const handleSave = (userData) => {
    if (userData.id) {
      setUsers(users.map((user) => (user.id === userData.id ? { ...user, ...userData } : user)));
    } else {
      setUsers([...users, { ...userData, id: users.length + 1 }]);
    }
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  // Definir columnas para la tabla
  const columns = [
    {
      name: "#",
      selector: (row) => row.id,
      sortable: true,
    },
    {
      name: "Nombre",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Usuario",
      selector: (row) => row.username,
      sortable: true,
    },
    {
      name: "Foto",
      cell: () => <UserCircle2 size={32} className={styles.userIcon1} />,
    },
    {
      name: "Perfil",
      selector: (row) => row.profile,
      sortable: true,
    },
    {
      name: "Estado",
      cell: (row) => (
        <span
          className={`${styles.statusBadge1} ${row.status === "Activado" ? styles.active1 : styles.inactive1}`}
          onClick={() => toggleStatus(row.id)}
          style={{ cursor: "pointer" }}
        >
          {row.status}
        </span>
      ),
      sortable: true,
    },
    {
      name: "Último login",
      selector: (row) => row.lastLogin,
      sortable: true,
    },
    {
      name: "Acciones",
      cell: (row) => (
        <div className={styles.actionButtons1}>
          <button className={styles.btnEdit1} onClick={() => handleEdit(row)}>
            <Pencil size={16} />
          </button>
          <button className={styles.btnDelete1} onClick={() => handleDelete(row)}>
            <Trash2 size={16} />
          </button>
        </div>
      ),
    },
  ];

  return (
    <div className={styles.containerFluid1}>
      <div className={styles.headerSection1}>
        <h2 className={styles.title1}>Administrar usuarios</h2>
      </div>
      <button
        className={styles.btnAdd1}
        onClick={() => {
          setSelectedUser(null);
          setIsModalOpen(true);
        }}
      >
        Agregar usuario
      </button>

      <div className={styles.tableWrapper1}>
        <DataTable
          columns={columns}
          data={users}
          pagination
          highlightOnHover
          striped
          noDataComponent="No hay datos disponibles"
        />
      </div>

      <UserModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedUser(null);
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  );
};

export default UserManagement;
