// UserManagement.js
"use client"
import { React, useState } from 'react';
import { Pencil, Trash2, UserCircle2 } from 'lucide-react';
import Swal from 'sweetalert2';
import styles from './usuarios.module.css';
import UserModal from './AddUserModal';

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 2,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 3,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Desactivado",
      lastLogin: "2017-12-11 12:05:32"
    }
  ]);

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (user) => {
    Swal.fire({
      title: '¿Estás seguro?',
      text: `¿Deseas eliminar al usuario ${user.name}?`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#c59951',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sí, eliminar',
      cancelButtonText: 'Cancelar'
    }).then((result) => {
      if (result.isConfirmed) {
        setUsers(users.filter(u => u.id !== user.id));
        Swal.fire(
          'Eliminado!',
          'El usuario ha sido eliminado.',
          'success'
        );
      }
    });
  };

  const toggleStatus = (userId) => {
    setUsers(users.map(user => {
      if (user.id === userId) {
        const newStatus = user.status === "Activado" ? "Desactivado" : "Activado";
        return { ...user, status: newStatus };
      }
      return user;
    }));
  };

  const handleSave = (userData) => {
    if (userData.id) {
      setUsers(users.map(user => 
        user.id === userData.id ? { ...user, ...userData } : user
      ));
    } else {
      setUsers([...users, { ...userData, id: users.length + 1 }]);
    }
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  return (
    <div className={styles.containerFluid1}>
      <div className={styles.headerSection1}>
        <h2 className={styles.title1}>Administrar usuarios</h2>
      </div>
      <button className={styles.btnAdd1} onClick={() => {
        setSelectedUser(null);
        setIsModalOpen(true);
      }}>
        Agregar usuario
      </button>

      <div className={styles.tableWrapper1}>
        <div className={styles.tableContainer1}>
          <table className={styles.usersTable1}>
            <thead>
              <tr>
                <th>#</th>
                <th>Nombre</th>
                <th>Usuario</th>
                <th>Foto</th>
                <th>Perfil</th>
                <th>Estado</th>
                <th>Último login</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.username}</td>
                  <td>
                    <UserCircle2 size={32} className={styles.userIcon1} />
                  </td>
                  <td>{user.profile}</td>
                  <td>
                    <span 
                      className={`${styles.statusBadge1} ${user.status === 'Activado' ? styles.active1 : styles.inactive1}`}
                      onClick={() => toggleStatus(user.id)}
                      style={{ cursor: 'pointer' }}
                    >
                      {user.status}
                    </span>
                  </td>
                  <td>{user.lastLogin}</td>
                  <td>
                    <div className={styles.actionButtons1}>
                      <button className={styles.btnEdit1} onClick={() => handleEdit(user)}>
                        <Pencil size={16} />
                      </button>
                      <button className={styles.btnDelete1} onClick={() => handleDelete(user)}>
                        <Trash2 size={16} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <UserModal 
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedUser(null);
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  );
};

export default UserManagement;
