import React, { useState, useMemo } from 'react';
import { Pencil, Trash2, UserCircle2, Search } from 'lucide-react';
import Swal from 'sweetalert2';
import './usuarios.module.css';
import UserModal from './AddUserModal';

const UserManagement = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 2,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Activado",
      lastLogin: "2017-12-11 12:05:32"
    },
    {
      id: 3,
      name: "Usuario Administrador",
      username: "admin",
      profile: "Administrador",
      status: "Desactivado",
      lastLogin: "2017-12-11 12:05:32"
    }
  ]);

  const filteredUsers = useMemo(() => {
    return users.filter(user => 
      user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.profile.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [users, searchTerm]);

  const totalPages = Math.ceil(filteredUsers.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentUsers = filteredUsers.slice(startIndex, endIndex);

  const handleEdit = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleDelete = (user) => {
    Swal.fire({
      title: '¿Estás seguro?',
      text: `¿Deseas eliminar al usuario ${user.name}?`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#c59951',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sí, eliminar',
      cancelButtonText: 'Cancelar'
    }).then((result) => {
      if (result.isConfirmed) {
        setUsers(users.filter(u => u.id !== user.id));
        Swal.fire(
          'Eliminado!',
          'El usuario ha sido eliminado.',
          'success'
        );
      }
    });
  };

  const toggleStatus = (userId) => {
    setUsers(users.map(user => {
      if (user.id === userId) {
        const newStatus = user.status === "Activado" ? "Desactivado" : "Activado";
        return { ...user, status: newStatus };
      }
      return user;
    }));
  };

  const handleSave = (userData) => {
    if (userData.id) {
      setUsers(users.map(user => 
        user.id === userData.id ? { ...user, ...userData } : user
      ));
    } else {
      setUsers([...users, { ...userData, id: users.length + 1 }]);
    }
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  return (
    <div className="containerFluid1">
      <div className="headerSection1">
        <h2 className="title1">Administrar usuarios</h2>
        
        <div className="flex items-center space-x-4 mr-8">
          <span style={{ color: '#c59951' }}>Mostrar</span>
          <select 
            className="records-select"
            value={itemsPerPage}
            onChange={(e) => setItemsPerPage(Number(e.target.value))}
          >
            <option value={10}>10</option>
            <option value={25}>25</option>
            <option value={50}>50</option>
          </select>
          <span style={{ color: '#c59951' }}>registros</span>
          
          <div className="search-container">
            <span style={{ color: '#c59951' }} className="mr-2">Buscar:</span>
            <div className="search-input-container">
              <input
                type="text"
                className="search-input"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Buscar..."
              />
              <Search className="search-icon" />
            </div>
          </div>
        </div>
      </div>

      <button className="btnAdd1" onClick={() => {
        setSelectedUser(null);
        setIsModalOpen(true);
      }}>
        Agregar usuario
      </button>

      <div className="tableWrapper1">
        <div className="tableContainer1">
          <table className="usersTable1">
            <thead>
              <tr>
                <th>#</th>
                <th>Nombre</th>
                <th>Usuario</th>
                <th>Foto</th>
                <th>Perfil</th>
                <th>Estado</th>
                <th>Último login</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {currentUsers.length > 0 ? (
                currentUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.name}</td>
                    <td>{user.username}</td>
                    <td>
                      <UserCircle2 size={32} className="userIcon1" />
                    </td>
                    <td>{user.profile}</td>
                    <td>
                      <span 
                        className={`statusBadge1 ${user.status === 'Activado' ? 'active1' : 'inactive1'}`}
                        onClick={() => toggleStatus(user.id)}
                        style={{ cursor: 'pointer' }}
                      >
                        {user.status}
                      </span>
                    </td>
                    <td>{user.lastLogin}</td>
                    <td>
                      <div className="actionButtons1">
                        <button className="btnEdit1" onClick={() => handleEdit(user)}>
                          <Pencil size={16} />
                        </button>
                        <button className="btnDelete1" onClick={() => handleDelete(user)}>
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="8" className="no-data">
                    Ningún dato disponible en esta tabla
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      <div className="pagination-container">
        <div className="pagination-info">
          Mostrando registros del {startIndex + 1} al {Math.min(endIndex, filteredUsers.length)} de un total de {filteredUsers.length}
        </div>
        <div className="pagination-buttons">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className="btnAdd1"
          >
            Anterior
          </button>
          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className="btnAdd1"
          >
            Siguiente
          </button>
        </div>
      </div>

      <UserModal 
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedUser(null);
        }}
        user={selectedUser}
        onSave={handleSave}
      />
    </div>
  );
};

export default UserManagement;
